$(".close_users").on("click",function(){
  $(".users").css("display","none")
  $(".list_users").css("display","block")
})
var IDLE_TIMEOUT = 10 *60   
var _idleSecondsCounter = 0
document.onclick = function() {
    _idleSecondsCounter = 0
}
document.onmousemove = function() {
    _idleSecondsCounter = 0
}
document.onkeypress = function() {
    _idleSecondsCounter = 0
}
window.setInterval(CheckIdleTime, 1000)
console.log(_idleSecondsCounter)
function CheckIdleTime() {
    _idleSecondsCounter++
    var oPanel = document.getElementById("SecondsUntilExpire")
    if (oPanel)
    var min = (IDLE_TIMEOUT - _idleSecondsCounter)%60;
    if(min<10){
      min = "0"+min
    }
    else{
      min = min;
    }
        oPanel.innerHTML = "0"+Math.trunc((IDLE_TIMEOUT - _idleSecondsCounter)/60)+" m "+ min+" s"
    if (_idleSecondsCounter >= IDLE_TIMEOUT) {
        document.location.href = "index.php?ctrl=security&action=logout"
    }
}